css:
.container {
  display: flex;
  width: 200px;
  align-items: stretch;
  /* flex-direction: column; */
}
.container div {
  flex: 1;
  text-align: center;
  border: 1px solid #ccc;
}
.container :first-child {
  font-size: 2em;
  background: #def;
}
.container :nth-child(2) {
  font-size: 1.5em;
}
